export function openPrintWindow(labelsHtml, orderNumber) {
    // Always start fresh to avoid global redeclaration issues across prints
    const existing = document.getElementById('print-frame');
    if (existing) existing.remove();

    const iframe = document.createElement('iframe');
    iframe.id = 'print-frame';
    iframe.style.display = 'none';
    document.body.appendChild(iframe);

    // ⬇️ Put your real data URL here
    const base64Watermark = "data:image/png;base64,";

    const htmlContent = `
    <html>
      <head>
        <title>Print Labels</title>
        <style>
          @page { size: 4in 2in; margin: 0; }
          body { font-family: Arial, sans-serif; text-align: center; margin: 0; padding: 0; }

          .label {
            position: relative;
            width: 4in; height: 2in;
            border: 1px solid black;
            display: flex; flex-direction: column; justify-content: center; align-items: center;
            padding: 5px; box-sizing: border-box;
            page-break-after: always; overflow: hidden; background: white;
          }
          .label h4, .barcode {
            position: relative; z-index: 1; margin: 3px 0; font-size: 14px;
          }
          .barcode { margin-top: 5px; height: 1in; }
          .print-date {
            position: absolute; bottom: 2px; right: 4px; font-size: 10px; color: #333;
            background-color: white; padding: 1px 3px; z-index: 3;
          }
          .watermark {
            position: absolute; top: 28%; left: 50%;
            width: 70%; height: 70%; object-fit: contain; opacity: 0.45;
            z-index: 0; pointer-events: none; transform: translate(-50%, -50%);
          }
        </style>
      </head>
      <body>
        ${labelsHtml}
        <script src="https://cdn.jsdelivr.net/npm/jsbarcode@3.11.5/dist/JsBarcode.all.min.js"></script>
        <script>
          window.onload = function () {
            const now = new Date();
            const fecha = now.toLocaleDateString('es-MX') + ' ' + now.toLocaleTimeString('es-MX', { hour12: false });

            const labelElements = document.querySelectorAll('.label');

            for (const label of labelElements) {
              // Add watermark first (behind everything)
              if ("${base64Watermark}".length > 0) {
                const img = document.createElement('img');
                img.src = "${base64Watermark}";
                img.className = "watermark";
                label.insertBefore(img, label.firstChild);
              }

              // Render barcode using the FINAL serial passed in the HTML
              const barcodeEl = label.querySelector('.barcode');
              const finalSerial = barcodeEl?.dataset.barcodeValue || '';
              if (finalSerial) {
                JsBarcode(barcodeEl, finalSerial, {
                  format: "CODE128",
                  width: 1.5,
                  height: 50,
                  margin: 8,
                  displayValue: true,
                  fontSize: 12
                });
                // Ensure visible text matches the barcode value
                const serialText = label.querySelector('.serial-text') || label.querySelector('h4:nth-of-type(3)');
                if (serialText) serialText.textContent = "Serial: " + finalSerial;
              }

              // Print date
              const dateDiv = document.createElement('div');
              dateDiv.className = 'print-date';
              dateDiv.textContent = fecha;
              label.appendChild(dateDiv);
            }

            setTimeout(() => window.print(), 300);
          };
        </script>
      </body>
    </html>
  `;

    const doc = iframe.contentWindow.document;
    doc.open();
    doc.write(htmlContent);
    doc.close();
}

export function triggerLabelPrinting(orderNumber) {
    console.log("Printing labels for Order:", orderNumber);

    const labels = [];
    $(".product-row").each(function () {
        const $row = $(this);
        const productName = $row.find("input[readonly]").val();
        const finalSerial = $row.find(".product-barcode").data("barcode-value"); // must already be FINAL serial

        if (productName && finalSerial) {
            labels.push(`
        <div class="label">
          <h4>Orden: ${orderNumber}</h4>
          <h4>Producto: ${productName}</h4>
          <h4 class="serial-text">Serial: ${finalSerial}</h4>
          <svg class="barcode" data-barcode-value="${finalSerial}"></svg>
        </div>
      `);
        }
    });

    if (labels.length > 0) openPrintWindow(labels.join(''), orderNumber);
    else console.warn("No hay datos para imprimir etiquetas.");
}